css:
.print-format th {
    background-color: transparent !important;
    border-bottom: 1px solid !important;
    border-top: none !important;
}
.print-format .ql-editor {
    padding-left: 0px;
    padding-right: 0px;
}

.print-format table {
    margin-bottom: 0px;
    }
.print-format .table-data tr:last-child { 
    border-bottom: 1px solid !important;
}

.print-format .table-inner tr:last-child {
    border-bottom:none !important;
}
.print-format .table-inner {
    margin: 0px 0px;
}

.print-format .table-data ul li { 
    color:#787878 !important;
}

.no-top-border {
    border-top:none !important;
}

.table-inner td {
    padding-left: 0px !important;    
    padding-top: 1px !important;
    padding-bottom: 1px !important;
    color:#787878 !important;
}

.total {
    background-color: lightgrey !important;
    padding-top: 4px !important;
    padding-bottom: 4px !important;
}

.print-format div {
    font-family: "Arial";
}

.print-format span {
    font-family: "Arial";
}

.print-format {
    font-size: 10.0pt;
    line-height: 1;
}

.print-format p {
    margin: 21px 0px 3px;
}


@page {
    margin-top: 0.6in;
    margin-bottom: 0.5in;
    margin-left: 0.75in;
    margin-right: 0.75in;    
}

