css:
.signatories {
    width: 100%;
    border: 0;
    line-height: .8;
    text-align: center;
}


@page {
    margin-top: 0.6in;
    margin-bottom: 0.5in;
    margin-left: 0.75in;
    margin-right: 0.75in;    
}

.print-format {
    font-size: 10.0pt;
}

.details {
    font-size: 15px;
    font-family: Tahoma, sans-serif;;
    line-height: 150%;
}

.certificate-footer {
    font-size: 15px;
    font-family: Tahoma, sans-serif;
    line-height: 140%;
    margin-top: 120px;
}

.company-address {
    color: #666666;
    font-size: 15px;
    font-family: Tahoma, sans-serif;;
}

/* Container holding the image and the text */
.container {
  position: relative;
  text-align: left;
}


.item-name {
  position: absolute;
  top: 1cm;
  left: 3.5cm;
  width: 5.5cm;
  height: 1.1cm;
  border: 0px solid;
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;
  font-weight: bold;
}

.item-ref {
  position: absolute;
  transform: rotate(-90deg);
