  position: absolute;
  top: 4.8cm;
  left: 7.6cm;
  letter-spacing: 1.25em;
  size: 200%;
  font-weight: bold;
}

/* PAYEE NAME*/
.payee-name {
  position: absolute;
  top: 5.7cm;
  left: 2cm;
  width: 7in;
  font-weight: bold;
  text-transform: uppercase;
}

/* PAYEE ADDRESS*/
.payee-address {
  position: absolute;
  top: 6.7cm;
  left: 2cm;
}

/* PAYEE ZIP*/
.payee-zip {
  position: absolute;
  top: 6.7cm;
  letter-spacing: 1.2em;
  left: 18.9cm;
}

/* PAYOR TIN */
.payor-tin {
  position: absolute;
  top: 8.7cm;
  left: 7.6cm;
  width: 8in;
  letter-spacing: 1.25em;
  font-weight: bold;
}

/* PAYOR NAME*/
.payor-name {
  position: absolute;
  top: 9.6cm;
  left: 2cm;
  width: 8in;
  font-weight: bold;
  text-transform: uppercase;
}

/* PAYOR ADDRESS*/
.payor-address {
  position: absolute;
  top: 10.6cm;
  left: 2cm;
}

/* PAYOR ZIP*/
.payor-zip {
  position: absolute;
  top: 10.6cm;
  letter-spacing: 1.2em;
  left: 18.9cm;
}

/* WITHHOLDING TAX DESCRIPTION*/
.wt-description {
  position: absolute;
  max-width: 4.5cm;
  overflow-wrap: normal;
  line-height: 1.9;
  top: 12.5cm;
  left: 1.3cm;
}

/* ATC */
.wt-atc {
  position: absolute;
  top: 12.5cm;
  left: 6.7cm;
}

/* Income Payments */
.income-payments {
  position: absolute;
  text-align: right;
  top: 12.5cm;
  right: 4.3cm;
}

/* Tax Withheld for the Quarter */
.tax-withheld {
  position: absolute;
  text-align: right;
  top: 12.5cm;
  right: 1.5cm;
}

/* Total Income Payments */
.total {
  position: absolute;
  text-align: right;
  top: 17.1cm;
  right: 1.5cm;
}

/* PAYOR Representative */
.payor-rep-sig {
  	position: absolute;
 	top: 24cm;
    width: 97%;
    text-align: center;
}

.payor-rep {
  position: absolute;
  width: 97%;
  text-align: center;
  font-weight: bold;
  text-transform: uppercase;
  top: 25.2cm;

}

/* PAYOR Representative TIN */
.payor-rep-tin {
  position: absolute;
  text-align: center;
  top: 25.2cm;
  left: 50%;
}

/* PAYEE Representative */
.payee-rep {
  position: absolute;
  width: 97%;
  text-align: center;
  text-transform: uppercase;
  top: 27.7cm;

}

/* Document Reference */
.reference {
  position: absolute;
  width: 95%;
  text-align: right;
  font-size: 100%;
  bottom: 1cm;
  border-top: 1px dotted;
}


.reprint-checker {
  position: absolute;
  width: 95%;
  text-align: left;
  font-size: 100%;
  bottom: 1cm;
}
